module.exports = {
    gameState: [],
    width: 10,
    height: 20,
    newState: function(){
        var state = [];
        for(var h = 0; h < this.height; h++){
            state[h] = [];
            for(var w = 0; w < this.width; w++){
                state[h][w] = { userId: 0, active: false };
            }
        }

        this.gameState = state;
        return state;
    },
    insert: function(){
        
    }
};
